mapper.select:
SELECT SUM(LAST_VAL) AS 'load' ,IND_B_NO AS indBNo FROM

    da_cons_gather_data_load
    da_cons_gather_data


where 1=1
AND IND_B_NO = #{param.indBNo}
AND CONS_NO in (
SELECT CONS_NO from cs_h_cons_info
WHERE DATA_SOURCE = #{param.dataSource}
)
AND DATA_DATE = #{param.dataDate}

SELECT SUM(TOTAL_Q) AS 'load' ,IND_B_NO AS indBNo FROM da_cons_day_power_data
where 1=1
AND IND_B_NO = #{indBNo}
AND CONS_NO in (
SELECT CONS_NO from cs_h_cons_info
WHERE DATA_SOURCE = #{dataSource}
)
AND DATA_DATE = #{dataDate}

SELECT TOTAL_Q AS 'load',IND_B_NO AS indBNo,DATA_DATE AS collectionTime from

    da_mp_day_power_data
    da_cons_day_power_data



    CONS_NO = #{consNo}

        AND MP_ID = #{mpId}

    AND IND_B_NO = #{indBNo}
    AND DATA_DATE = #{date}

limit 1

SELECT LAST_VAL as 'load',IND_B_NO AS indBNo,LAST_TIME as 'collectionTime' FROM



            da_mp_gather_data_load
            da_cons_gather_data_load





            da_mp_gather_data
            da_cons_gather_data




    CONS_NO = #{consNo}

        AND MP_ID = #{mpId}

    AND IND_B_NO = #{indBNo}
    AND DATA_DATE = #{date}

limit 1

SELECT * FROM



            da_mp_gather_data_load
            da_cons_gather_data_load





            da_mp_gather_data
            da_cons_gather_data




    CONS_NO = #{consNo}

        AND MP_ID = #{mpId}

    AND IND_B_NO = #{indBNo}
    AND DATA_DATE = #{date}

limit 1
